import App from 'next/app';
import React from 'react';
import 'semantic-ui-css/semantic.min.css';

import { Provider } from 'react-redux';
import '../public/static/css/styles.css';
import parseCookies from '../libs/cookies/parseCookies';
import withReduxStore from '../libs/redux/with-redux-store';


class AgniCartApp extends App {
    static async getInitialProps({ Component, ctx }) {
      const isServer = typeof window === 'undefined';
    //   if (isServer) {
    //     const cookies = parseCookies(ctx.req);
    //     const accessToken = cookies[cookieName] || '';
    //     if (accessToken) {
    //       try {
    //         const headers = getHeaders(accessToken);
    //         const { data } = await Axios.get(Api.userDetail, { headers });
    //         const isLoggedIn = true;
    //         const user = data;
    //         ctx.reduxStore.dispatch(updateUser({ isLoggedIn, accessToken, userDetails: user }));
    //       } catch (error) {
    //         console.log(error);
    //         ctx.reduxStore.dispatch(loginFailed());
    //       }
    //     }
    //   }
  
      const pageProps = Component.getInitialProps ? await Component.getInitialProps(ctx) : {};
      return { pageProps };
    }
  
  
    render() {
      const { Component, pageProps, reduxStore } = this.props;
      return (
        <Provider store={reduxStore}>
          <Component {...pageProps} />
        </Provider>
      );
    }
  }
  
  export default withReduxStore(AgniCartApp);
