import React from 'react';
import Link from 'next/link';
import { withRouter } from 'next/router';
import {
  Image, Grid, Form, Input, Message, Header, Divider,
} from 'semantic-ui-react';


class SignUp extends React.PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      isLoading: false,
      email: '',
      emailErrors: [],
      password: '',
      passwordErrors: [],
      errors: [],
    };
  }
  render() {
    const { isLoading, email, emailErrors, password, passwordErrors, errors } = this.state;
    return (
      <div className="loginSignup">
      <div style={{
        position: 'absolute', top: 0, left: 0, bottom: 0, right: 0,
      }}
      >
        <div style={{
          width: '100%', float: 'right', height: '100%', display: 'flex', alignItems: 'center', justifyContent: 'center',
        }}
        >
               <Grid centered>
      <Grid.Row verticalAlign="middle">
        <Grid.Column mobile={16} tablet={8} computer={16}>
          <Header color="yellow" as="h1" textAlign="center">Create an account</Header>
          <Form onSubmit={this.onSubmit} error={errors.length > 0} loading={isLoading}>
            <Form.Field>
                <lable style={{color: "yellow"}}>Email Address</lable><Input name="email" type="text" placeholder="Email Address" onChange={this.onChange} value={email} error={emailErrors.length ? emailErrors.join() : null} /></Form.Field>
            <Form.Field>
            <lable style={{color: "yellow"}}>Password</lable>
                <Input name="password" type="password" placeholder="Password" onChange={this.onChange} value={password} error={passwordErrors.length ? passwordErrors.join() : null} /></Form.Field>
            <Message hidden={!errors.length} error list={errors} />
            <Form.Button content="Sign Up" positive fluid></Form.Button>
          </Form>
        </Grid.Column>
      </Grid.Row>
      <Grid.Row>
        <Grid.Column mobile={16} tablet={8} computer={8}>
          <Divider horizontal><p style={{color: "white"}}>Or</p></Divider>
          <Grid columns={1}>
            <Grid.Column>
            <Header inverted as="h5" textAlign="center">
                  Already have an account?
                  <Link href="/user/login" passHref><a title="Log In"> Log In</a></Link>
                </Header>
            </Grid.Column>
          </Grid>
        </Grid.Column>
      </Grid.Row>
    </Grid>
        </div>
        </div>
        </div>
    );
  }
}

export default withRouter(SignUp);
